import Header from './header';
import Footer from './footer';
import Nav from './nav'

const layoutStyle = {
  background: '#ff3',
  border: '1px solid #DDD'
};

const Layout = props => (
  <div >
    <Header />
    <Nav />
       {props.content}
    <Footer/>   
  </div>
);

export default Layout;
